import { useState, useEffect } from "react";
import AddSvg from "../../assets/svgs/add";
import AddDiary from "../AddDiary/AddDiary";
import "./MyDiarys.css";
const MyDiarys = () => {
  const [isModalOpened, setIsModalOpened] = useState<boolean>(false)
  function handleClick(){
    const modal = document.getElementById("modal")
    if(modal){
      modal.style.display = "flex"
      setIsModalOpened(true)
    }
    
  }
  useEffect(() => {
    console.log("useEffect");
    
    const handleCloseModal = (e: any) => {
      console.log("modal opened");

      const modal = document.getElementById("modal");

      if (e.target !== modal && modal) {
        modal.style.display = "none";
        setIsModalOpened(false)
      }
    };

    window.addEventListener("click", handleCloseModal);

    return window.removeEventListener("click", handleCloseModal);

  }, [isModalOpened]);

  return (
    <div className="diarys">
      <div className="diary-btn flex justify-center">
        <span className="diary-name ">Food tracking</span>
      </div>
      <div onClick={handleClick} className="diary-btn grid grid-ratio items-center">

        <div className="add-svg">
          <AddSvg />
        </div>

        <span className="span1 ml-5">Add Diary</span>

      </div>
      
      <AddDiary></AddDiary>  
      
    </div>

  );
};
export default MyDiarys;
